import React, { useState, useEffect } from 'react'
import '../public/style/components/header.css'
import { Row, Col, Menu } from 'antd'
import Router from 'next/router'
import Link from 'next/link'
import axios from 'axios'
import api from '../config/api'
// import '@ant-design/icons'

const Header = () => {
  const [navBar, setNavBar] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(api.getTypeInfo).then(res => {
        return res.data.data
      })
      setNavBar(result)
    }
    fetchData();
  }, [])

  const handleClick = (e) => {
    if (e.key == 0) {
      Router.push('/index')
    } else {
      Router.push(`/list?id=${e.key}`)
    }
  }

  return (
    <div className='header'>
      <Row type="flex" justify="center" >
        <Col xs={24} sm={24} md={8} lg={10} xl={10}>
          <div className='text' style={{ fontSize: 24, color: cornflowerblue, }}>Escape</div>
          <div className='text'>
            <span>into amazing experiences</span>
          </div>
        </Col>
        <Col xs={0} sm={0} md={10} lg={8} xl={6} className="memu-div">
          <Menu mode="horizontal" onClick={handleClick}>
            <Menu.Item key="0">
              {/* <AndroidOutlined /> */}
                    首页
                </Menu.Item>
            {
              navBar.map(item => {
                return (
                  <Menu.Item key={item.Id} >
                    {/* <{{item.icon}} /> */}
                    {item.typeName}
                  </Menu.Item>
                )
              })
            }

          </Menu>
        </Col>
      </Row>
    </div>
  )
}

export default Header
