import React, { useState } from 'react'
import axios from 'axios'
import Head from 'next/head'
import Link from 'next/link'
import Header from '../components/Header'
import Aurhor from '../components/Author'
import servicePath from '../config/api'
import marked from 'marked'
import '../public/style/pages/index.css'
import { Row, Col, List } from 'antd'
import hljs from "highlight.js";
import 'highlight.js/styles/monokai-sublime.css';
import { HourglassOutlined, YoutubeOutlined, FireOutlined } from '@ant-design/icons'
const Home = (list) => {
  const [mylist, setList] = useState(list.data)
  const renderer = new marked.Renderer();
  marked.setOptions({
    renderer: renderer,
    gfm: true,
    pedantic: false,
    sanitize: false,
    tables: true,
    breaks: false,
    smartLists: true,
    smartypants: false,
    sanitize: false,
    xhtml: false,
    highlight: function (code) {
      return hljs.highlightAuto(code).value;
    }
  });

  return (
    <div>
      <Head>
        <title>Home</title>
      </Head>
      <Header />
      <Row className='comm-main' justify="center">
        <Col className='comm-left' xs={24} sm={24} md={12} lg={12} xl={12}>
          <List
            header={<div className='comm-title'>最新发表</div>}
            dataSource={mylist}
            itemLayout='vertical'
            renderItem={item => <List.Item>
              <div className='list-title'>
                <Link href={{ pathname: '/detailed', query: { id: item.id } }}>
                  <a>{item.title}</a>
                </Link>
              </div>
              <div className='list-icon'>
                <span><HourglassOutlined />{item.addTime}</span>
                <span><YoutubeOutlined />{item.typeName}</span>
                <span><FireOutlined />{item.view_count}</span>
              </div>
              <div className='list-context' dangerouslySetInnerHTML={{ __html: marked(item.introduce) }}>
              </div>
            </List.Item>}
          />
        </Col>
        <Col className='comm-right' xs={0} sm={0} md={8} lg={6} xl={4}>
          <Aurhor />
        </Col>
      </Row>
    </div>
  )
}
Home.getInitialProps = async () => {
  const promise = new Promise(resolve => {
    axios(servicePath.getArticleList).then(res => {
      resolve(res.data)
    })
  })
  return await promise
}
export default Home
